    @staticmethod
    def get_bounding_box_from_list_of_bounding_boxes(bbox_list):
        errors = ""
        geom_type = "UNSET"

        min_Lat = None  # float(bounds[0])  # miny
        min_Long = None  # float(bounds[1])  # minx
        max_Lat = None  # float(bounds[2])  # maxy
        max_Long = None  # float(bounds[3])  # maxx

        try:
            for current_bbox in bbox_list:
                current_bbox__min_Lat   = current_bbox['min_Lat']
                current_bbox__max_Lat   = current_bbox['max_Lat']
                current_bbox__min_Long  = current_bbox['min_Long']
                current_bbox__max_Long  = current_bbox['max_Long']

                if (min_Lat == None):
                    min_Lat = current_bbox__min_Lat
                if (max_Lat == None):
                    max_Lat = current_bbox__max_Lat
                if (current_bbox__min_Lat < min_Lat):
                    min_Lat = current_bbox__min_Lat
                if (current_bbox__max_Lat > max_Lat):
                    max_Lat = current_bbox__max_Lat

                if (min_Long == None):
                    min_Long = current_bbox__min_Long
                if (max_Long == None):
                    max_Long = current_bbox__max_Long
                if (current_bbox__min_Long < min_Long):
                    min_Long = current_bbox__min_Long
                if (current_bbox__max_Long > max_Long):
                    max_Long = current_bbox__max_Long


            pass
        except:
            sys_error_info = sys.exc_info()
            errors += "  There was a problem when calculating the bounding box.  System Error Message: " + str(sys_error_info)
            pass

        return_obj = {}
        return_obj['geometry_type'] = "BoundingBoxes"
        return_obj['min_Lat'] = min_Lat
        return_obj['max_Lat'] = max_Lat
        return_obj['min_Long'] = min_Long
        return_obj['max_Long'] = max_Long
        return_obj['errors'] = errors
        return return_obj

