    @staticmethod
    def get_shapes_list(geometries_list, min_Lat, min_Long, height_Lat_in_cords, height_in_Pixels, width_Long_in_cords, width_in_Pixels):
        shapes_list = []
        for geometry in geometries_list:
            new_shape = {}

            # Check the Type
            geometry_type = geometry['type']

            if (geometry_type == 'Polygon'):
                cord_pairs = geometry['coordinates'][0]
                current_geometry_reprojected = []
                for cord_pair in cord_pairs:
                    lat = float(cord_pair[1])
                    long = float(cord_pair[0])
                    lat_partial = lat - min_Lat
                    lat_Pixel_Reproj = (lat_partial / height_Lat_in_cords) * height_in_Pixels
                    long_partial = long - min_Long
                    long_Pixel_Reproj = (long_partial / width_Long_in_cords) * width_in_Pixels
                    cord_Pixel_Reproj = (long_Pixel_Reproj, lat_Pixel_Reproj)
                    current_geometry_reprojected.append(cord_Pixel_Reproj)
                # END OF    for cord_pair in cord_pairs:
                new_shape['type'] = geometry['type']
                new_coords = []
                new_coords.append(current_geometry_reprojected)
                new_shape['coordinates'] = new_coords
            # END OF    if (geometry_type == 'Polygon'):

            if (geometry_type == 'MultiPolygon'):
                current_MultiPolygon_Reprojected = []
                for polygon in geometry['coordinates']:
                    geom_data_json = polygon[0]
                    current_geometry_reprojected = []
                    for cord_pair in geom_data_json:
                        lat = cord_pair[1]
                        long = cord_pair[0]
                        lat_partial = lat - min_Lat
                        lat_Pixel_Reproj = (lat_partial / height_Lat_in_cords) * height_in_Pixels
                        long_partial = long - min_Long
                        long_Pixel_Reproj = (long_partial / width_Long_in_cords) * width_in_Pixels
                        cord_Pixel_Reproj = (long_Pixel_Reproj, lat_Pixel_Reproj)
                        current_geometry_reprojected.append(cord_Pixel_Reproj)
                    # END OF    for cord_pair in geom_data_json:
                    current_MultiPolygon_Reprojected.append([current_geometry_reprojected])
                # END OF    for polygon in geometry['coordinates']:
                new_shape['type'] = geometry['type']
                new_shape['coordinates'] = []
                new_shape['coordinates'] = current_MultiPolygon_Reprojected
            # END OF    if (geometry_type == 'MultiPolygon'):

            # Append this new shape
            shapes_list.append(new_shape)
        # END OF        for geometry in geometries_list:


        return shapes_list

